import client from './sanity';


const blogFields = `
  title,
  subtitle,
  'slug': slug.current,
  date,
  author,
  'coverImage': coverImage.asset->url
  
`

export async function getAllBlogs() {
  const results = await client
    .fetch(`*[_type == "blog"]{${blogFields}}`);
  return results;
}
